import React, { useState } from 'react';
import style from '../Tool/Style';
import {Avatar, Button, Container, Grid, TextField, Typography} from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';

const Login = () => {

const [usuario, setUsuario] = useState({
    Email : '',
    Password: ''
});    


const pasarValorMemoria = e => {
    const {name, value} = e.target;
    setUsuario(anterior => ({
        ...anterior,
        [name] : value
    }))
}

const ejecutaBoton = () => {
    console.log('Datos de las cajas de texto: ', usuario);
}


  return (  
    <Container maxWidth ='xs'>
        <div style={style.paper}>
            <Avatar style={style.avatar}>
                <LockOutlinedIcon style={style.icon} />
            </Avatar>
            <Typography component='h1' variant='h5'>
                Login de Usuario
            </Typography>
        </div>
        <form style={style.form}>
            <TextField name='Email' onChange={pasarValorMemoria} value={usuario.Email} variant='outlined' label='Ingrese su username' fullWidth margin='normal' />
            
            
            <Grid container justify='center'>
            <Grid item xs={12} md={6}>
                <TextField type='Password' name='txtPassword' onChange={pasarValorMemoria} value={usuario.Password} variant='outlined' label='Ingrese su password' fullWidth margin='normal' />
                    </Grid>
                    <Grid item xs={12} md={6}>
                    <Button type='submit' onClick={ejecutaBoton} variant='contained' color='primary' style={style.submit}>
                        Enviar
                    </Button>
                </Grid>
            </Grid>
        </form>

    </Container>
  )
}

export default Login
