import React, { useEffect, useState } from "react";
import axios from 'axios';



function Perfil(prop){

    const [paises, obtenerPaises] = useState([]);
    const [status, cambiarStatus] = useState(false);

    useEffect ( () => {

       axios.get('https://restcountries.com/v3.1/lang/spanish') 
            .then(resultado => {
                obtenerPaises(resultado.data);
                cambiarStatus(true);
            })
            .catch(error => {
                cambiarStatus(true);
            })

        
    } , [] )


    if(status){
        return(

            <table style={{border: 'solid', borderRadius: 10, marginLeft: 20, padding: 30}}>
                <header color="" style={{textAlign: "center"}}>Paises que su idioma es espa√±ol</header>
                <tr>
                    <th style={{textAlign:'left'}}>Nombre Comun</th>
                    <th>&nbsp; &nbsp;</th>
                    <th style={{textAlign:'left'}}>Nombre Completo</th>
                </tr>
            
                {paises.map((pais, indice) => 

                    <tr>
                        <td key={indice} style={{textAlign:'left'}}>
                            {pais.name.common}
                        </td>
                        <td>&nbsp; &nbsp;</td>
                        <td key={indice} style={{textAlign: 'left'}}>
                            {pais.name.official}
                        </td>
                    </tr>

                )}

            </table>
        );
    }else{
        return(<h1>Esta cargando los valores del servicio....</h1>);
    }

}


export default Perfil;
