import React, { useEffect, useState } from "react";
import axios from 'axios';



function Perfil(prop){

    const [paises, obtenerPaises] = useState([]);
    const [status, cambiarStatus] = useState(false);

    useEffect ( () => {

       axios.get('https://restcountries.com/v3.1/lang/spanish') 
            .then(resultado => {
                obtenerPaises(resultado.data);
                cambiarStatus(true);
            })
            .catch(error => {
                cambiarStatus(true);
            })

        
    } , [] )


    if(status){
        return(
            <ul>
                {paises.map((pais, indice) => 
                    <table>
                        <tr>
                            <th>Nombre Comun</th>
                            <th>&nbsp;&nbsp;&nbsp;</th>
                            <th>Nombre Completo</th>
                        </tr>
                        <tr>
                            <td key={indice} style={{textAlign:'left'}}>
                                {pais.name.common}
                            </td>
                            <td>&nbsp;&nbsp;&nbsp;</td>
                            <td key={indice} >
                                {pais.name.official}
                            </td>
                        </tr>
                    </table>
                    
                )}
            </ul>
        );
    }else{
        return(<h1>Esta cargando los valores del servicio....</h1>);
    }


}


export default Perfil;
