import { Button, IconButton, Toolbar, Typography, makeStyles } from '@material-ui/core'
import React from 'react'
import theme from '../../../theme/theme'
import FotoUsuarioTemp from '../../../theme/theme'


// esto es para manejar la respnsividad con MaterialDesing

const useStyles = makeStyles((theme) => ({
  seccionDesktop : {
    display : 'none',
    [theme.breakpoints.up('md')] : {
      display : 'flex'
    }
  },
  seccionMobile : {
    display : 'flex',
    [theme.breakpoints.up('md')] : {
      display : 'none'
    }
  },
  grow : {
    flexGrow : 1
  },
  avatarSize : {
    width : 40,
    height : 40
  }

}))

const BarSesion = () => {

  const classes = useStyles();

  return (
    <Toolbar>
        <IconButton color='inherit'>
          <i className='material-icons'>menu</i>
        </IconButton>

        <Typography variant='h6'>Cursos Oline</Typography>

        <div className={classes.grow}></div>
        <Button color='inherit'>
          Salir
        </Button>
        <Button color='inherit'>
          {"Miguel Silva"}
        </Button>

    </Toolbar>
  )
}

export default BarSesion
