import React, { useEffect, useState } from "react";
import axios from 'axios';



function Perfil(prop){

    const [paises, obtenerPaises] = useState([]);
    const [status, cambiarStatus] = useState(false);

    useEffect ( () => {

       axios.get('https://restcountries.com/v3.1/lang/spanish') 
            .then(resultado => {
                obtenerPaises(resultado.data);
                cambiarStatus(true);
            })
            .catch(error => {
                cambiarStatus(true);
            })

        
    } , [] )


    if(status){
        return(
            <ul>
                {paises.map((pais, indice) => 
                    <table>
                        <tr>
                            <td>
                                <li key={indice}>Nombre Comun: {pais.name.common}</li>
                            </td>
                        </tr>
                        <tr>
                            <td>
                                <li key={indice}>Nombre Oficial: {pais.name.official}</li>
                            </td>
                        </tr>
                    </table>
                    
                )}
            </ul>
        );
    }else{
        return(<h1>Esta cargando los valores del servicio....</h1>);
    }


}


export default Perfil;
