import React, { useEffect, useState } from 'react'
import style from '../Tool/Style'
import { Button, Container, Grid, TextField, Typography } from '@material-ui/core';
import { obtenerUsuarioActual } from '../../actions/UsuarioActions';

export const PerfilUsuario = () => {

    const [usuario, setUsuario] = useState({
        nombreCompleto : '',
        token : '',
        email : '',
        username : ''
        //imagen : ''
    })

    const IngresarValoresMemoria = e =>{
        const {name, value} = e.target;
        setUsuario(anterior => ({
            ...anterior,
            [name] : value
        }));
    }

    useEffect( () => {
        obtenerUsuarioActual().then(response => {
            console.log('Esta es la data del usuario actual: ', response);
            setUsuario(response.data);
        })
    })


  return (
    <Container component='main' maxWidth='md' justify='center'>
        <div style={style.paper}>
            <Typography component='h1' variant='h5'>
                Perfil de Usuario
            </Typography>
        </div>
        <form style={style.form}>
            <Grid container spacing={2}>

                <Grid item xs={12} md={12}>
                    <TextField name='nombreCompleto' onChange={IngresarValoresMemoria} value={usuario.nombreCompleto} variant='outlined' label='Ingrese nombre completo' fullWidth />
                </Grid>

                {/* <Grid item xs={12} md={6}>
                    <TextField name='txtApellidos' variant='outlined' label='Ingrese apellidos' fullWidth />
                </Grid> */}

                <Grid item xs={12} md={6}>
                    <TextField name='email' onChange={IngresarValoresMemoria} value={usuario.email} variant='outlined' label='Ingrese su email' fullWidth />
                </Grid>

                {/* <Grid item xs={12} md={6}>
                    <TextField name='txtPassword' variant='outlined' label='Ingrese su password' fullWidth />
                </Grid> */}

                <Grid item xs={12} md={6}>
                    <TextField name='username' onChange={IngresarValoresMemoria} value={usuario.username} variant='outlined' label='Ingrese su username' fullWidth />
                </Grid>
                
            </Grid>

            <Grid container justify='center'>
                <Grid item xs={12} md={6}>
                    <Button type='submit' variant='contained' size='large' color='primary' fullWidth style={style.submit}>
                        Guardar Datos
                    </Button>
                </Grid>
            </Grid>
            
        </form>

    </Container>
    
  )
};

export default PerfilUsuario
