import React, { useEffect, useState } from "react";
import axios from 'axios';



function Perfil(prop){

    const [paises, obtenerPaises] = useState([]);
    const [status, cambiarStatus] = useState(false);

    useEffect ( () => {

       axios.get('https://restcountries.com/v3.1/lang/spanish') 
            .then(resultado => {
                obtenerPaises(resultado.data);
                cambiarStatus(true);
            })
            .catch(error => {
                cambiarStatus(true);
            })

        
    } , [] )


    if(status){
        return(

            <table style={{border: 'solid', borderRadius: 10}}>
                        <tr>
                            <th style={{textAlign:'left', width: 500}}>Nombre Comun</th>
                            
                            <th style={{textAlign:'left', width: 500}}>Nombre Completo</th>
                        </tr>
            
            <ul>
                {paises.map((pais, indice) => 
                        <tr>
                            <td key={indice} style={{textAlign:'left', width: 200}}>
                                {pais.name.common}
                            </td>
                            
                            <td key={indice} style={{textAlign: 'left', width: 500}}>
                                {pais.name.official}
                            </td>
                        </tr>
                )}
            </ul>
            </table>
        );
    }else{
        return(<h1>Esta cargando los valores del servicio....</h1>);
    }

}


export default Perfil;
